ader__img {\n    width: 18px;\n    height: 18px;\n    filter: drop-shadow(1px 1px 3px rgba(0, 0, 0, 0.3));\n  }\n  .com__content__left__card__content {\n    padding: 5px 10px;\n    background: linear-gradient(\n      to right,\n      rgb(180, 200, 251) 0%,\n      rgb(164, 185, 251) 50%,\n      rgb(180, 200, 251) 100%\n    );\n    background-color: rgba(198, 211, 255, 0.87);\n  }\n  .com__content__left__card__row {\n    display: flex;\n    margin-bottom: 2px;\n  }\n\n  .com__content__left__card__img {\n    width: 14px;\n    height: 14px;\n    margin-right: 5px;\n  }\n  .com__content__left__card__text {\n    font-size: 10px;\n    line-height: 14px;\n    color: #0c327d;\n    &.black {\n      color: #000;\n    }\n    &.bold {\n      font-weight: bold;\n    }\n\n    &.link:hover {\n      cursor: pointer;\n      color: #2b72ff;\n      text-decoration: underline;\n    }\n  }\n  .com__content__right {\n    height: 100%;\n    overflow: auto;\n    background-color: #fff;\n    flex: 1;\n  }\n  .com__content__right__card__header {\n    width: 300px;\n    font-weight: 700;\n    padding: 2px 0 3px 12px;\n    position: relative;\n    &:after {\n      content: '';\n      display: block;\n      background: linear-gradient(to right, #70bfff 0, #fff 100%);\n      position: absolute;\n      bottom: 0;\n      left: -12px;\n      height: 1px;\n      width: 100%;\n    }\n  }\n  .com__content__right__card__content {\n    display: flex;\n    align-items: center;\n    padding-right: 0;\n    flex-wrap: wrap;\n    padding: 15px 15px 0;\n  }\n  .com__content__right__card__item {\n    display: flex;\n    align-items: center;\n    width: 200px;\n    margin-bottom: 15px;\n    height: auto;\n  }\n  .com__content__right__card__img {\n    width: 45px;\n    height: 45px;\n    margin-right: 5px;\n  }\n  .com__content__right__card__text {\n    white-space: nowrap;\n    height: 100%;\n  }\n  .com__content__right__card--me {\n    .com__content__right__card__header:after,\n    .com__content__right__card__h